mapper.select:
SELECT record.*,record_treatmentcar.id rid,record_treatmentcar.record_id,record_treatmentcar.treatmentcar_id,site.id sid,site.site_name,site.serial_number,site.address,site.telephone telephoneOfSite,car.id cid,car.license,car.driver_id,user.id uid,user.realname,user.telephone FROM record LEFT JOIN site ON record.site_id=site.id LEFT JOIN record_treatmentcar ON record.id=record_treatmentcar.record_id LEFT JOIN car ON record_treatmentcar.treatmentcar_id=car.id LEFT JOIN user ON car.driver_id=user.id WHERE user.id=#{driverId} AND
record.status=#{status}
record.status!=#{status}
ORDER BY record.allocation_time DESC

mapper.update:
UPDATE record
SET car_id=#{carId}
Where id=#{recordId}

UPDATE record
SET car_id=#{carId}
Where record.status=2 AND record.site_id=#{siteId}
AND record.car_id IS NULL

UPDATE record
SET status=#{status}
WHERE id=#{recordId}

UPDATE record
SET status=#{status},


		disposal_time=#{time}


		finish_time=#{time}


where id=#{recordId}

UPDATE record
SET car_num=car_num-1
WHERE id=#{recordId}
