mapper.select:
SELECT record.*,car.id,car.driver_id,car.license,car.brand,user.id,user.realname,user.telephone,site.id,site.site_name,site.serial_number,site.address,site.telephone telephoneOfSite FROM record LEFT JOIN site ON record.site_id=site.id LEFT JOIN car ON record.car_id=car.id LEFT JOIN `user` ON car.driver_id=`user`.id WHERE record.del_status=0 AND car.driver_id=#{driverId} AND
record.status=#{status}
record.status!=#{status}
ORDER BY record.allocation_time DESC

mapper.update:
UPDATE record
SET car_id=#{carId}
Where
record.del_status=0 AND id=#{recordId}

UPDATE record
SET car_id=#{carId}
Where
record.del_status=0 AND record.status=2 AND record.site_id=#{siteId}
AND record.car_id IS NULL

UPDATE record
SET status=#{status}
WHERE id=#{recordId} AND del_status=0

UPDATE record
SET status=#{status},


		disposal_time=#{time}


		finish_time=#{time}


where id=#{recordId}
