mapper.update:
UPDATE site
SET status=#{status}
WHERE id=#{siteId}

mapper.select:
SELECT
count(*)
FROM site
WHERE serial_number=#{serialNumber}
OR site_name=#{siteName}

mapper.update:
UPDATE site
set sensor_id_set=#{sensorIdSet}
WHERE
id=#{siteId}

mapper.select:
		SELECT *
		FROM site


                site.id=#{siteId}


                site.status=#{status}


        ORDER BY site.status DESC;

SELECT id,status
FROM site
ORDER BY status DESC;
