mapper.select:
SELECT sludge.*,record.id,record.site_id,car.id,car.license,car.driver_id,user.id,user.realname,user.telephone,site.id,site.site_name,sludge_function.id,sludge_function.`function` FROM sludge LEFT JOIN record ON sludge.record_id=record.id LEFT JOIN car ON sludge.transcar_id=car.id LEFT JOIN site ON record.site_id=site.id LEFT JOIN `user` ON car.driver_id=`user`.id LEFT JOIN sludge_function ON sludge.function_id=sludge_function.id WHERE sludge.status!=6 AND site.id=#{siteId}
and sludge.minor_mud_warehouse_id=#{minorWareHouseId}
and sludge.status in (0,1)
and sludge.status in (2,3)
ORDER BY sludge.produce_time DESC

